import "./barraNav.css";
import BarraNav from "./barraNav";
import axios from 'axios';
import { useState, useEffect } from 'react';
import { Button } from 'react-bootstrap';



function Personajes() {

    const [personajes, setpersonajes] = useState([]);
    useEffect(() => {
        axios.get("http://localhost:3001/personajes").then(response=> {
                setpersonajes(response.data.personajes);
            });
    }, []);

    let listaPersonajes = <h1>No hay personajes.</h1>;

    if (personajes.length > 0) {

        listaPersonajes = personajes.map(dato => (
            <div class="fila">
            <div class="columna">
            <div className="Tarjeta">
            <div className="Favorito">
                <>
                <br></br>
                <img src={dato.image}></img>
                <br></br>
                <h2>Nombre: {dato.name}</h2>
                <h2>Genero: {dato.gender}</h2> 
                <h2>Especie: {dato.species}</h2>
                <br></br>         
                </>
                </div>
            </div>
            </div>
            </div>

        ));
    }
    return (
        <>
        <BarraNav />
        <br></br>
        <h1>Lista de Personajes</h1>
        <br></br>
        {listaPersonajes}
        <div className="Footer">
            <p>@ carlos</p>
            </div>
        </>
    );

}
export default Personajes;
