import "./barraNav.css";
import BarraNav from "./barraNav";
import axios from 'axios';
import { useState, useEffect } from 'react';


function Favorito() {

    const [personajes, setpersonajes] = useState([]);
    useEffect(() => {
        axios.get("http://localhost:3000/personajes").then(response=> {
                setpersonajes(response.data.personajes);
            });
    }, []);

    let listpersonajes = <h1>No hay personajes</h1>;

    if (personajes.length > 0) {

        listpersonajes = personajes.map(dato => (
            <div class="fila">
            <div class="columna">
            <div className="Tarjeta">
            <div className="Favorito">
                <>
                <br></br>
                <img src={dato.image}></img>
                <br></br>
                <h2>Nombre: {dato.name}</h2>
                <h2>Genero: {dato.gender}</h2> 
                <h2>Especie: {dato.species}</h2>
                <Button className="boton" href={`http://localhost:3000/personajes/borrarPersonajes/${dato._id}`}>Eliminar</Button>
                <br></br>         
                </>
                </div>
            </div>
            </div>
            </div>

        ));
    }



    return (
        <>
        <BarraNav />
        <br></br>
        <h1>Lista de Personajes</h1>
        <br></br>
        {listpersonajes}
        <div className="Footer">
            <p>@ Luis Alfaro Vera Trabajo Final React - Node</p>
            </div>
        </>
    );

}

export default Favorito;
