import "./barraNav.css";
import "./index.css";
import BarraNav from "./barraNav";
import axios from 'axios';
import { useState, useEffect } from 'react';

function Favorito() {
    const [personajes, setpersonajes] = useState([]);
    useEffect(() => {
        axios.get("http://localhost:3000/favoritop").then(response => {
            setpersonajes(response.data.personajes);
        });
    }, []);

    let listaPersonajes = personajes.data;

    if (personajes != null) {

        listaPersonajes =
            <>
                <div className="Tarjeta">
                    <div className="Favorito">
                        <>
                            <br></br>
                            <img src={personajes.image}></img>
                            <br></br>
                            <h3> Nombre: {personajes.name}</h3>
                            <h3> Genero: {personajes.gender}</h3>
                            <h3> Especie: {personajes.species}</h3>
                        </>
                    </div>
                </div>
            </>
    }
    else {
        <h1>no</h1>
    }

    return (
        <>
            <BarraNav />
            <h1>personaje favorito</h1>
            <br></br>
            <div>
                {listaPersonajes}
            </div>
            <div className="Footer">
                <footer texto = "@carlos rodriguez" />
            </div>
        </>
    );
}
export default Favorito;
